    const diminuer = () => {
        setCounter(counter - 1);
    }

    const reinitialiser = () => {
        setCounter(100);
    }

    const renderResetButton = () => {
        return (
            <button className={`button ${counter === 100 ? 'hidden' : 'visible'}`}onClick={reinitialiser}>RÃ©initialiser</button>
        );
    }

    return (
        <div className='center'>
            <h1>Bienvenue dans mon appli</h1>
            <button className='button' onClick={() => setCounter(counter + 1)}>Ajouter</button>
            {renderResetButton()}
