import React from 'react';
import { useState } from 'react';
import "./style.css";
import "./right_section.css";
import "./left_section.css";

const RightSection = ({ page }: { page: number }) => {
    switch (page) {
        case 0:
            return <h2>Section 0</h2>;
        case 1:
            return <h2>Section 1</h2>;
        case 2:
            return <h2>Section 2</h2>;
        default:
            return <h2>Default Section</h2>;
    }
};

export default function Main() {
    const [page, setPage] = useState<number>(0);

    return (
        <div className="container">
            <div className="left-section">
                <div className="left-section-block1">
                    <button className="title-link" onClick={() => setPage(0)}>Portfolio</button>
                    <div className="head-link">
                        <a href="https://github.com/xmarano">GitHub</a>
                        <a href="http://linkedin.com/in/leogregori">LinkedIn</a>
                        <a href="mailto:leo.gregori@epitech.eu">Email</a>
                        <a href="assets/cv_leo-gregori.pdf" download>CV</a>
                    </div>
                </div>
                <div className="left-section-block2">
                    <p>PROJECTS</p>
                    <button onClick={() => setPage(1)}>Deliveries</button>
                    <button onClick={() => setPage(2)}>Portfolio</button>
                    <button onClick={() => setPage(3)}>You&Me</button>
                </div>
                <div className="left-section-block2">
                    <p>EPITECH</p>
                    <button onClick={() => setPage(4)}>My Rpg</button>
                    <button onClick={() => setPage(5)}>My Hunter</button>
                    <button onClick={() => setPage(6)}>Corewar</button>
                    <button onClick={() => setPage(7)}>42sh</button>
                </div>
            </div>
            <div className="right-section">
                <RightSection page={page} />
            </div>
            <div className="date">May '24</div>
        </div>
    );
}
